import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';


class Header extends Component {
    
    render() {
        return (
            <div>
                <Link to='/' style={{padding: '5px'}}>
                    Home
                </Link>
                <Link to='/profile' style={{padding: '5px'}}>
                    Profile
                </Link>
                <Link to='/component1' style={{padding: '5px'}}>
                    Component 1
                </Link>
                <Link to='/container1' style={{padding: '5px'}}>
                    Container 1
                </Link>
                <Link to='/form1' style={{padding: '5px'}}>
                    Form 1
                </Link>
                <Link to='/hookscontainer' style={{padding: '5px'}}>
                    Hooks Container
                </Link>
                <Link to='/hooksform' style={{padding: '5px'}}>
                    Hooks Form
                </Link>
                <Link to='/renderlist' style={{padding: '5px'}}>
                    List
                </Link>
                <Link to='/privateroute' style={{padding: '5px'}}>
                    Private Route
                </Link>
                {
                    !this.props.is_authenticated
                    ? <button onClick={() => this.props.auth.login()}> Login </button>
                    : <button onClick={() => this.props.auth.logout()}> Logout </button>
                }
                <br/>
                <br/>
                <br/>
            </div>
        )
    }
}

function mapStateToProps(state) {
    return {
        is_authenticated: state.auth_reducer.is_authenticated
    }
}

export default connect(mapStateToProps)(Header);
